import { PrismaClient } from "@prisma/client";

const prisma = new PrismaClient();

const addons = [
    {
        namespace: "default",
        name: "mysql",
        fancyName: "MySQL",
        description:
            "The most popular open source database. With its proven performance, reliability and ease-of-use, MySQL has become the leading database choice for web-based applications",
        icon: "data:image/svg+xml;utf8;base64,",
        image: "mysql",
        versions: ["5.7.44", "5.7", "5", "8.0.35", "8.0", "8.2.0", "8.2", "8"],
        replicas: 1,
        volumes: [
            {
                path: "/var/lib/mysql",
                size: "1Gi",
                accessMode: ["ReadWriteOnce"],
            },
        ],
        env: {
            MYSQL_ROOT_PASSWORD: "root",
            MYSQL_USER: "mysql",
            MYSQL_PASSWORD: "mysql",
            MYSQL_DATABASE: "mysql_db",
        },
        ports: [
            {
                name: "mysql",
                port: 3306,
                targetPort: 3306,
            },
        ],
    },
    {
        namespace: "default",
        name: "mondo",
        fancyName: "MongoDB",
        description: "A NoSQL database program, MongoDB uses JSON-like documents with schemata.",
        icon: "data:image/svg+xml;utf8;base64,",
        image: "mongodb",
        versions: ["4.4.25", "4.4", "4", "5.0.22", "5.0", "5", "6.0.11", "6.0", "6", "7.0.3", "7.0", "7"],
        replicas: 1,
        volumes: [
            {
                path: "/data/db",
                size: "1Gi",
                accessMode: ["ReadWriteOnce"],
            },
        ],
        env: {
            MONGO_INITDB_ROOT_USERNAME: "mongod",
            MONGO_INITDB_ROOT_PASSWORD: "mongod",
            MONGO_INITDB_DATABASE: "mongod",
        },
        ports: [
            {
                name: "mongod",
                port: 27017,
                targetPort: 27017,
            },
        ],
    },
    {
        namespace: "default",
        name: "postgresql",
        fancyName: "PostgreSQL",
        description: "An object-relational database management system (ORDBMS) with an emphasis on extensibility and standards-compliance",
        icon: "data:image/svg+xml;utf8;base64,",
        image: "postgres",
        versions: ["12", "12.17", "13", "13.13", "14", "14.10", "15", "15.5", "16", "16.1"],
        replicas: 1,
        volumes: [
            {
                path: "/var/lib/postgresql/data",
                size: "1Gi",
                accessMode: ["ReadWriteOnce"],
            },
        ],
        env: {
            POSTGRES_PASSWORD: "postgres",
            POSTGRES_USER: "postgres",
            POSTGRES_DB: "postgres",
        },
        ports: [
            {
                name: "postgresql",
                port: 5432,
                targetPort: 5432,
            },
        ],
    },
];

async function main() {
    const databaseType = await prisma.addonType.create({
        data: {
            type: "DATABASE",
        },
    });

    for await (const addon of addons) {
        const addonCreated = await prisma.addon.create({
            data: {
                name: addon.name,
                fancyName: addon.fancyName,
                description: addon.description,
                icon: addon.icon,
                image: addon.image,
                type: {
                    connect: { id: databaseType?.id },
                },
            },
        });

        for await (const version of addon.versions) {
            await prisma.addonVersion.create({
                data: {
                    version,
                    addon: {
                        connect: { id: addonCreated.id },
                    },
                },
            });
        }

        for await (const [key, value] of Object.entries(addon.env)) {
            await prisma.addonEnv.create({
                data: {
                    key,
                    value,
                    addon: {
                        connect: { id: addonCreated.id },
                    },
                },
            });
        }

        for await (const volume of addon.volumes) {
            await prisma.addonVolume.create({
                data: {
                    path: volume.path,
                    addon: {
                        connect: { id: addonCreated.id },
                    },
                },
            });
        }
    }
}

main()
    .then(async () => {
        await prisma.$disconnect();
    })
    .catch(async (e) => {
        console.error(e);
        await prisma.$disconnect();
        process.exit(1);
    });
